    }

  return (
    <div className="task-wrapper">
      <TaskCreate
        addTask={addTask}
      />
      {todos.map(item => {
          return(
              <Task
                key={item.id}
                item={item}
                removeTask={removeTask}
                changeDone={changeDone}
                changeLike={changeLike}
              />
          )
      })}
    </div>
  );
}

export default TaskWrapper;
