// import { useState } from 'react';
import './Task.css';

function Task({id, text, done, like, removeTask}) {



  return (
    <div 
        className='task'
        key={id}
    >
        <p
            onDoubleClick={like ? 'task__like like' : 'task__like'}
        >
            {text}
        </p>
        <input
            type='checkbox'
            className={done ? 'task__done checked' : 'task__done'}
        />
        <button
            onClick={removeTask}
        >
            delete
        </button>
    </div>
  );
}

export default Task;
