// import { useState } from 'react';
import './Task.css';

function Task({item, removeTask, changeDone, changeLike}) {



  return (
    <div 
        className='task'
        key={item.id}
    >
        <p
            className={item.like ? 'task__like like' : 'task__like'}
            onDoubleClick={changeLike}
        >
            {item.text}
        </p>
        <input
            type='checkbox'
            className={item.done ? 'task__done checked' : 'task__done'}
            onChange={changeDone}
        />
        <button
            onClick={() => removeTask(item.id)}
        >
            delete
        </button>
    </div>
  );
}

export default Task;
