    }

  return (
    <div className="task-wrapper">
      <TaskCreate
        addTask={addTask}
      />
      {todos.map(item => {
          return(
              <Task
                key={item.id}
                text={item.text}
                done={item.done}
                like={item.like}
                removeTask={removeTask}
              />
          )
      })}
    </div>
  );
}

